import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import {ReactComponent as ArrowLeft} from "../assets/image/chevron-left.svg"

const NotePages = () => {
  const { id } = useParams();
  console.log(id)
  const [note, setNote] = useState({});
  useEffect(() => {
    const getNote = async () => {
    if(id==='new') return
    let result = await fetch(`/api/notes/${id}`);
    let data = await result.json();
    setNote(data);
  };
  getNote
  }, [id]);


  let updateNote=async()=>{
    fetch(`/api/notes/${id}/update/`,{
        method:'PUT',
        headers: {
            'Content-Type':'application/json'
        },
        body: JSON.stringify(note)

    })
  }
  let deleteNote=async()=>{
    fetch(`/api/notes/${id}/delete/`,{
        method:'DELETE',
        headers: {
            'Content-Type':'application/json'
        }
    })
    navigate("/")
  }
  const navigate=useNavigate()
  const handleSubmit=()=>{
    if(id!=="new" && !note.body){
        deleteNote()
    }
    else if(id!=="new"){
    updateNote()}
    navigate("/")
  }
  return (
    <div className="note">
        <div className="note-header">
           <h3><ArrowLeft onClick={handleSubmit}/></h3>
           {id!=='new'?( <button onClick={deleteNote}>Delete</button>):(<button onClick={deleteNote}>Done</button>)}
          
        </div>
      <textarea onChange={e=>{setNote({...note,'body':e.target.value})}} defaultValue={note?.body}></textarea>
    </div>
  );
};

export default NotePages;
