import React from "react";
import { Link } from "react-router-dom";

const ListItem = ({ note }) => {
  return (
    <>
      <Link to={`note/${note.id}`}>
        <div className="notes-list-item">
        <h1>{notenote.body.slice(0,50)}</h1>
        </div>
      </Link>
    </>
  );
};

export default ListItem;
